@import './_mixins.scss';
@import url('https://fonts.googleapis.com/css2?family=Nunito&display=swap');

//Reset
*,
*::after,
*::before {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  //This defines 1rem (10px) 62,5% of initial 16px
  font-size: 62.5%;
  font-family: 'Nunito', sans-serif;
  background-image: url('./../images/wall.jpg');
  box-shadow: inset 0 0 0 1000px rgba(0, 0, 0, 0.7);
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
}

body {
  position: relative;
  min-height: 100vh;
  @include flex-config(flex, column, center, center);
}

.glow-sign {
  position: absolute;
  top: 10%;
  left: 10%;
  @include flex-config(flex, column, center, center);

  @include respond(phone) {
    top: 2%;
    left: 50%;
    transform: translate(-50%, -2%);
  }

  &__span {
    font-size: 6rem;
    color: #aaaaaa;
    animation: glow 0.08s ease-in-out infinite alternate;
  }
}

.container-cta {
  position: absolute;
  top: 30%;
  right: 50%;
  transform: translate(50%, -30%);

  @include respond(tab-land) {
    top: 40%;
  }

  @include respond(tab-port) {
    top: 42%;
  }

  @include respond(phone) {
    top: 45%;
  }
}

.form {
  @include flex-config($display: flex, $direction: column);

  &__select {
    font-size: 1.6rem;
    font-family: sans-serif;
    font-weight: 700;
    color: #444;
    line-height: 1.3;
    padding: 1rem 2.4rem 1rem 1rem;
    width: 100%;
    margin-top: 3rem;
    border: 1px solid #aaa;
    border-radius: 1rem;
    -moz-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    background-color: #fff;
    background-image: url('data:image/svg+xml;charset=US-ASCII,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22292.4%22%20height%3D%22292.4%22%3E%3Cpath%20fill%3D%22%23007CB2%22%20d%3D%22M287%2069.4a17.6%2017.6%200%200%200-13-5.4H18.4c-5%200-9.3%201.8-12.9%205.4A17.6%2017.6%200%200%200%200%2082.2c0%205%201.8%209.3%205.4%2012.9l128%20127.9c3.6%203.6%207.8%205.4%2012.8%205.4s9.2-1.8%2012.8-5.4L287%2095c3.5-3.5%205.4-7.8%205.4-12.8%200-5-1.9-9.2-5.5-12.8z%22%2F%3E%3C%2Fsvg%3E'),
      linear-gradient(to bottom, #ffffff 0%, #e5e5e5 100%);
    background-repeat: no-repeat, repeat;
    background-position: right 0.7em top 50%, 0 0;
    background-size: 0.65rem auto, 100%;
    cursor: pointer;

    @include respond(phone) {
      font-size: 1.4rem;
    }

    &:focus {
      box-shadow: 0 0 0 0.1rem;
      color: rgb(0, 0, 0);
      outline: none;
    }
  }
}

.start-btn {
  width: 100%;
  font-size: 2.5rem;
  padding: 1.5rem 7rem;
  margin-top: 5rem;
  border-radius: 1rem;
  text-transform: uppercase;
  transition: all 0.2s ease;
  cursor: pointer;

  @include respond(tab-land) {
    margin-top: 2rem;
  }

  @include respond(phone) {
    font-size: 1.3rem;
    padding: 1rem;
    margin-top: 3rem;
  }

  &:hover {
    transform: translateY(0.2rem);
    box-shadow: -1px 3px 15px -2px rgba(238, 123, 15, 0.96);
  }

  &:focus {
    outline: none;
  }
}

.countdown {
  padding-top: 5rem;
  font-size: 7rem;

  @include respond(phone) {
    font-size: 3rem;
    padding-top: 2rem;
  }
}

.container-quiz {
  display: none;
  width: 50%;
  justify-content: center;
  margin: 5rem 0 2rem 0;
  border-radius: 2rem;
  background-color: rgba(255, 255, 255, 0.8);

  @include respond(tab-land) {
    width: 90%;
    margin-bottom: 10rem;
  }

  @include respond(phone) {
    margin-top: 2rem;
  }
}

.quiz {
  /* display: flex;
  flex-direction: column; */
  @include flex-config($display: flex, $direction: column);
  width: 80%;
  padding: 2rem 0;

  &__current-question {
    display: none;
    text-align: center;
    font-size: 1.8rem;
    color: #000000;
    font-weight: bold;
  }

  &__category {
    font-size: 3rem;
    color: #5f5f5f;
    padding: 2rem 0;
    text-align: center;

    @include respond(phone) {
      font-size: 2.5rem;
    }
  }

  &__question {
    font-size: 3rem;
    padding: 1rem 2rem 3rem 2rem;
    text-align: center;

    @include respond(phone) {
      font-size: 1.8rem;
      padding: 1rem 2rem 1rem 2rem;
    }
  }

  &__loader {
    font-size: 10px;
    margin: 50px auto;
    text-indent: -9999em;
    width: 11em;
    height: 11em;
    border-radius: 50%;
    background: #ffffff;
    background: -moz-linear-gradient(
      left,
      #ffffff 10%,
      rgba(255, 255, 255, 0) 42%
    );
    background: -webkit-linear-gradient(
      left,
      #ffffff 10%,
      rgba(255, 255, 255, 0) 42%
    );
    background: -o-linear-gradient(
      left,
      #ffffff 10%,
      rgba(255, 255, 255, 0) 42%
    );
    background: -ms-linear-gradient(
      left,
      #ffffff 10%,
      rgba(255, 255, 255, 0) 42%
    );
    background: linear-gradient(
      to right,
      #ffffff 10%,
      rgba(255, 255, 255, 0) 42%
    );
    position: relative;
    -webkit-animation: load3 1.4s infinite linear;
    animation: load3 1.4s infinite linear;
    -webkit-transform: translateZ(0);
    -ms-transform: translateZ(0);
    transform: translateZ(0);

    &:before {
      width: 50%;
      height: 50%;
      background: #ffffff;
      border-radius: 100% 0 0 0;
      position: absolute;
      top: 0;
      left: 0;
      content: '';
    }

    &:after {
      background: #0dc5c1;
      width: 75%;
      height: 75%;
      border-radius: 50%;
      content: '';
      margin: auto;
      position: absolute;
      top: 0;
      left: 0;
      bottom: 0;
      right: 0;
    }
  }

  &__list {
    padding-bottom: 5rem;

    @include respond(phone) {
      padding-bottom: 2.5rem;
    }
  }

  &__list-item {
    font-size: 2rem;
    font-weight: bold;
    list-style: none;
    background-color: #fff;
    margin-top: 1rem;
    padding: 2rem 1rem;
    text-align: center;
    border-radius: 2rem;
    cursor: pointer;

    @include respond(phone) {
      font-size: 1.4rem;
      padding: 1rem 0;
    }
  }

  &__nextBtn {
    width: 50%;
    font-size: 2rem;
    text-transform: uppercase;
    letter-spacing: 0.4rem;
    margin: 0 auto;
    background-color: #021bff;
    color: #fff;
    padding: 2rem 3rem;
    margin-bottom: 4rem;
    border-radius: 2rem;
    border: none;
    cursor: pointer;

    @include respond(phone) {
      width: 100%;
      font-size: 1.5rem;
      letter-spacing: 0.2rem;
      padding: 2rem 2rem;
      margin-bottom: 1rem;
    }

    &:focus {
      outline: none;
    }
  }
}
.correct-answer {
  background-color: #08ff08;
  border: 5px solid #000000;
}

.incorrect-answer {
  background-color: #ff0000;
  border: 5px solid #000000;
}

.disabled {
  pointer-events: none;
  opacity: 0.6;
}

.game-over {
  display: none;
  @include flex-config($direction: column, $align: center);
  padding: 10rem 2rem;

  &__paragraph {
    font-size: 4rem;
  }

  &__score {
    font-size: 4rem;
    margin-top: 2rem;
    color: #0d2763;
    padding: 0 2rem;

    @include respond(tab-land) {
      font-size: 3rem;
      text-align: center;
    }

    @include respond(phone) {
      font-size: 2rem;
    }
  }

  &__btn {
    font-size: 3rem;
    padding: 2rem 5rem;
    background-color: #021bff;
    color: #fff;
    margin-top: 4rem;
    border: none;
    border-radius: 2.5rem;
    text-transform: uppercase;
    letter-spacing: 0.4rem;
    cursor: pointer;
    transition: all 0.2s ease;

    @include respond(phone) {
      font-size: 1.5rem;
      letter-spacing: 0.2rem;
      padding: 2rem 3rem;
    }

    &:focus {
      outline: none;
    }

    &:hover {
      transform: translateY(0.2rem);
      box-shadow: -1px 3px 15px -2px rgba(238, 123, 15, 0.96);
    }
  }
}

@keyframes glow {
  from {
    text-shadow: 0 0 6px rgba(202, 228, 225, 0.92),
      0 0 30px rgba(202, 228, 225, 0.34), 0 0 12px rgba(248, 146, 13, 0.52),
      0 0 21px rgba(242, 168, 30, 0.92), 0 0 34px rgba(238, 167, 13, 0.78),
      0 0 54px rgba(242, 168, 30, 0.92);
  }
  to {
    text-shadow: 0 0 6px rgba(202, 228, 225, 0.98),
      0 0 30px rgba(202, 228, 225, 0.42), 0 0 12px rgba(248, 146, 13, 0.58),
      0 0 22px rgba(242, 168, 30, 0.84), 0 0 38px rgba(238, 167, 13, 0.88),
      0 0 60px rgba(242, 168, 30, 1);
  }
}

@keyframes load3 {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}
@keyframes load3 {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }
  100% {
    -webkit-transform: rotate(360deg);
    transform: rotate(360deg);
  }
}

